  String skip() throws ParseException {
    StringBuffer sb = new StringBuffer();
    int nesting = 0;
    Token t = getToken(0);
    if (t.image != null) {
    sb.append(t.image);
    }
    do {
    t = getNextToken();
    if (t.kind == EOF)
        break;
    sb.append(t.image);
    if (t.kind == LBRACE)
        nesting++;
    else if (t.kind == RBRACE)
        nesting--;
    else if (t.kind == SEMICOLON && nesting <= 0)
        break;
    }
    while ((t.kind != RBRACE) || (nesting > 0));

    return sb.toString();
  }

